import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './AttendanceTracker.css';

const AttendanceTracker = ({ contractId }) => {
  const [employees, setEmployees] = useState([]);
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [tempAttendance, setTempAttendance] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [workingDays, setWorkingDays] = useState(0);
  const [isFormOpen, setIsFormOpen] = useState(false);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/employees`);
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, [contractId]);

  useEffect(() => {
    const fetchAttendanceRecords = async () => {
      try {
        const month = selectedDate.getMonth();
        const year = selectedDate.getFullYear();
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/attendance`, {
          params: { month, year }
        });
        setAttendanceRecords(response.data);
        setTempAttendance(response.data); // Initialize tempAttendance with fetched data
      } catch (error) {
        console.error('Error fetching attendance records:', error);
      }
    };

    fetchAttendanceRecords();
  }, [contractId, selectedDate]);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleWorkingDaysChange = (e) => {
    setWorkingDays(e.target.value);
  };

  const handleTempAttendanceChange = (employeeId, field, value) => {
    const newTempAttendance = [...tempAttendance];
    const index = newTempAttendance.findIndex(record => record.employeeId._id === employeeId);
    if (index !== -1) {
      newTempAttendance[index][field] = value;
    } else {
      const employee = employees.find(emp => emp._id === employeeId);
      newTempAttendance.push({
        contractId,
        employeeId,
        month: selectedDate.getMonth(),
        year: selectedDate.getFullYear(),
        workingDays,
        dailyWage: field === 'dailyWage' ? value : employee.dailyWage,
        attendance: field === 'attendance' ? value : 0,
      });
    }
    setTempAttendance(newTempAttendance);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const records = tempAttendance.map(record => ({
        contractId,
        employeeId: record.employeeId,
        month: selectedDate.getMonth(),
        year: selectedDate.getFullYear(),
        workingDays,
        dailyWage: record.dailyWage,
        attendance: record.attendance,
      }));
      const response = await axios.post(`http://localhost:5000/api/contracts/${contractId}/attendance`, { records });
      setAttendanceRecords(response.data);
      setTempAttendance(response.data); // Update tempAttendance with saved data
      setIsFormOpen(false);
    } catch (error) {
      console.error('Error saving attendance records:', error);
    }
  };

  const toggleForm = () => {
    setIsFormOpen(!isFormOpen);
  };

  return (
    <div className="attendance-tracker">
      <h2>Attendance Tracker</h2>
      <button className="add-attendance-button" onClick={toggleForm}>
        {isFormOpen ? 'Cancel' : 'Add Attendance'}
      </button>
      {isFormOpen && (
        <form className="collapsible-form active" onSubmit={handleFormSubmit}>
          <label>
            Select Month/Year:
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              dateFormat="MM/yyyy"
              showMonthYearPicker
            />
          </label>
          <label>
            Number of Working Days:
            <input type="number" value={workingDays} onChange={handleWorkingDaysChange} required />
          </label>
          <table>
            <thead>
              <tr>
                <th>Employee Name</th>
                <th>Daily Wage</th>
                <th>Attendance</th>
              </tr>
            </thead>
            <tbody>
              {employees.map((employee) => {
                const attendanceRecord = tempAttendance.find(record => record.employeeId._id === employee._id) || {};
                return (
                  <tr key={employee._id}>
                    <td>{employee.name}</td>
                    <td>
                      <input
                        type="number"
                        value={attendanceRecord.dailyWage || employee.dailyWage}
                        onChange={(e) => handleTempAttendanceChange(employee._id, 'dailyWage', e.target.value)}
                      />
                    </td>
                    <td>
                      <input
                        type="number"
                        value={attendanceRecord.attendance || 0}
                        onChange={(e) => handleTempAttendanceChange(employee._id, 'attendance', e.target.value)}
                      />
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <button type="submit">Save Attendance</button>
        </form>
      )}
      <div className="attendance-records">
        <h3>Attendance Records</h3>
        <table>
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Daily Wage</th>
              <th>Attendance</th>
              <th>Month/Year</th>
            </tr>
          </thead>
          <tbody>
            {attendanceRecords.map(record => (
              <tr key={record._id}>
                <td>{record.employeeId.name}</td>
                <td>{record.dailyWage}</td>
                <td>{record.attendance}</td>
                <td>{`${record.month + 1}/${record.year}`}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AttendanceTracker;
