import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import Sidebar from '../components/Sidebar';
import ContractDetails from '../components/ContractDetails';
import AttendanceTracker from '../components/AttendanceTracker';
import EmployeeList from '../components/EmployeeList';
import SalaryTable from '../components/SalaryTable';
import './ContractPage.css';

const ContractPage = () => {
  const { id } = useParams();
  const [contract, setContract] = useState(null);
  const [contracts, setContracts] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [month, setMonth] = useState(new Date().getMonth() + 1);
  const [year, setYear] = useState(new Date().getFullYear());
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    const fetchContracts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contracts');
        setContracts(response.data);
      } catch (error) {
        console.error('Error fetching contracts:', error);
      }
    };

    fetchContracts();
  }, []);

  useEffect(() => {
    const fetchContract = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${id}`);
        setContract(response.data);
        console.log('Fetched contract:', response.data);
      } catch (error) {
        console.error('Error fetching contract:', error);
      }
    };

    const fetchEmployees = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${id}/employees`);
        console.log('Fetched employees:', response.data);
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchContract();
    fetchEmployees();
  }, [id]);

  const handleSidebarToggle = (isOpen) => {
    setIsSidebarOpen(isOpen);
  };

  const handleEditEmployee = (updatedEmployee) => {
    setEmployees((prevEmployees) => {
      const existingEmployeeIndex = prevEmployees.findIndex((emp) => emp._id === updatedEmployee._id);
      if (existingEmployeeIndex > -1) {
        return prevEmployees.map((emp) =>
          emp._id === updatedEmployee._id ? updatedEmployee : emp
        );
      } else {
        return [...prevEmployees, updatedEmployee];
      }
    });
  };

  const handleDeleteEmployee = (employeeId) => {
    setEmployees((prevEmployees) =>
      prevEmployees.filter((emp) => emp._id !== employeeId)
    );
  };

  const handleMonthChange = (e) => {
    setMonth(e.target.value);
  };

  const handleYearChange = (e) => {
    setYear(e.target.value);
  };

  const handleGenerateSalaries = async () => {
    try {
      console.log(`Generating salaries for contractId: ${contract._id}, month: ${month}, year: ${year}`);
      const response = await axios.post(`http://localhost:5000/api/contracts/${contract._id}/salaries/generate`, { month, year });
      console.log('Salaries generated:', response.data);
      alert('Salaries generated successfully!');
    } catch (error) {
      console.error('Error generating salaries:', error);
      alert('Error generating salaries. Please try again.');
    }
  };

  if (!contract) {
    return <div>Loading...</div>;
  }

  console.log('Contract ID in ContractPage:', contract._id); // Log contract ID to verify it's not undefined

  return (
    <div className={`contract-page-container ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
      <Sidebar contracts={contracts} onToggle={handleSidebarToggle} />
      <div className="contract-page">
        <div className="contract-header">
          <div className="left">
            <span>Start Date: {new Date(contract.startDate).toLocaleDateString()}</span>
            <span>End Date: {new Date(contract.endDate).toLocaleDateString()}</span>
          </div>
          <div className="middle">
            <span>Contract No.: {contract.contract_no}</span>
          </div>
          <div className="right">
            <span className="value">Value: INR {contract.value.toLocaleString()}</span>
          </div>
        </div>
        <ContractDetails contract={contract} />
        <EmployeeList
          employees={employees}
          contractId={contract._id}
          onEdit={handleEditEmployee}
          onDelete={handleDeleteEmployee}
        />
        <AttendanceTracker contractId={contract._id} />
        <div className="salary-section">
          <h2>Generate Monthly Salary</h2>
          <div className="date-selector">
            <label>
              Month:
              <select value={month} onChange={handleMonthChange}>
                {Array.from({ length: 12 }, (_, i) => i + 1).map(m => (
                  <option key={m} value={m}>{m}</option>
                ))}
              </select>
            </label>
            <label>
              Year:
              <input type="number" value={year} onChange={handleYearChange} />
            </label>
          </div>
          <button onClick={handleGenerateSalaries}>Generate Salaries</button>
          <SalaryTable contractId={contract._id} month={month} year={year} />
        </div>
        <div className="navigation-links">
          <Link to="/">Go to Homepage</Link>
        </div>
      </div>
    </div>
  );
};

export default ContractPage;
