import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './AttendanceDisplay.css';

const AttendanceDisplay = ({ contractId }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [attendanceRecords, setAttendanceRecords] = useState([]);

  useEffect(() => {
    const fetchAttendanceRecords = async () => {
      try {
        const month = selectedDate.getMonth();
        const year = selectedDate.getFullYear();
        console.log(`Fetching attendance records for contractId: ${contractId}, month: ${month + 1}, year: ${year}`); // Debug log
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/attendance`, {
          params: { contractId, month, year }
        });
        console.log('Fetched attendance records from API:', response.data); // Debug log
        setAttendanceRecords(response.data);
      } catch (error) {
        console.error('Error fetching attendance records:', error);
      }
    };

    fetchAttendanceRecords();
  }, [contractId, selectedDate]);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  return (
    <div className="attendance-display">
      <h2>View Attendance</h2>
      <label>
        Select Month/Year:
        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          dateFormat="MM/yyyy"
          showMonthYearPicker
        />
      </label>
      <table>
        <thead>
          <tr>
            <th>Employee Name</th>
            <th>Daily Wage</th>
            <th>Attendance</th>
            <th>Month/Year</th>
          </tr>
        </thead>
        <tbody>
          {attendanceRecords.map(record => (
            <tr key={record._id}>
              <td>{record.employeeId.name}</td>
              <td>{record.dailyWage}</td>
              <td>{record.attendance}</td>
              <td>{`${record.month + 1}/${record.year}`}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceDisplay;
