import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AttendanceDisplay = ({ contractId }) => {
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [selectedMonthYear, setSelectedMonthYear] = useState('');
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    console.log('Fetching attendance for contractId:', contractId, 'month:', selectedMonthYear.split('-')[1], 'year:', selectedMonthYear.split('-')[0]);

    const fetchAttendance = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/attendance`, {
          params: {
            month: selectedMonthYear.split('-')[1],
            year: selectedMonthYear.split('-')[0]
          }
        });
        console.log('Fetched attendance records from DB:', response.data);
        setAttendanceRecords(response.data);
      } catch (error) {
        console.error('Error fetching attendance records:', error);
      }
    };

    const fetchEmployees = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/employees`);
        console.log('Fetched employees:', response.data);
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    if (selectedMonthYear && contractId) {
      fetchAttendance();
      fetchEmployees();
    }
  }, [contractId, selectedMonthYear]);

  return (
    <div className="attendance-display">
      <h3>Attendance Records</h3>
      <input
        type="month"
        value={selectedMonthYear}
        onChange={(e) => setSelectedMonthYear(e.target.value)}
      />
      {attendanceRecords.length === 0 ? (
        <p>No attendance records found for the selected month.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Daily Wage</th>
              <th>Attendance</th>
            </tr>
          </thead>
          <tbody>
            {attendanceRecords.map((record) => {
              const employee = employees.find((emp) => emp._id === record.employeeId);
              return (
                <tr key={record._id}>
                  <td>{employee ? employee.name : 'Unknown'}</td>
                  <td>{record.dailyWage}</td>
                  <td>{record.attendance}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AttendanceDisplay;
