import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { CalendarIcon, DollarSignIcon, FileTextIcon, UsersIcon } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from 'components/ui/card';
import { Button } from 'components/ui/button';
import { Select } from 'components/ui/select';
import { Input } from 'components/ui/input';
import Sidebar from '../components/Sidebar';
import ContractDetails from '../components/ContractDetails';
import AttendanceTracker from '../components/AttendanceTracker';
import EmployeeList from '../components/EmployeeList';
import GenerateSalary from '../components/GenerateSalary';
import DisplaySalary from '../components/DisplaySalary';

const ContractPage = () => {
  const { id } = useParams();
  const [contract, setContract] = useState(null);
  const [contracts, setContracts] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [month, setMonth] = useState(new Date().getMonth() + 1);
  const [year, setYear] = useState(new Date().getFullYear());
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [contractsRes, contractRes, employeesRes] = await Promise.all([
          axios.get('http://localhost:5000/api/contracts'),
          axios.get(`http://localhost:5000/api/contracts/${id}`),
          axios.get(`http://localhost:5000/api/contracts/${id}/employees`)
        ]);

        setContracts(contractsRes.data);
        setContract(contractRes.data);
        setEmployees(employeesRes.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [id]);

  const handleAddEmployee = async (newEmployee) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/contracts/${contract._id}/employees`, newEmployee);
      setEmployees([...employees, response.data]);
    } catch (error) {
      console.error('Error adding employee:', error);
    }
  };

  const handleEditEmployee = (updatedEmployee) => {
    setEmployees(prevEmployees => 
      prevEmployees.map(emp => emp._id === updatedEmployee._id ? updatedEmployee : emp)
    );
  };

  const handleDeleteEmployee = (employeeId) => {
    setEmployees(prevEmployees => prevEmployees.filter(emp => emp._id !== employeeId));
  };

  const handleMonthChange = (value) => {
    setMonth(parseInt(value));
  };

  const handleYearChange = (e) => {
    setYear(parseInt(e.target.value));
  };

  if (!contract) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar contracts={contracts} onToggle={setIsSidebarOpen} />
      <div className={`flex-1 overflow-auto transition-all duration-300 ${isSidebarOpen ? 'ml-64' : 'ml-0'}`}>
        <div className="container mx-auto px-4 py-8">
          <Card className="mb-8">
            <CardHeader className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white">
              <CardTitle className="text-2xl flex items-center justify-between">
                <span className="flex items-center">
                  <FileTextIcon className="mr-2" />
                  Contract Details
                </span>
                <span className="text-lg">Contract No.: {contract.contract_no}</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="p-6">
              <div className="grid grid-cols-3 gap-4 text-sm">
                <div className="flex items-center">
                  <CalendarIcon className="mr-2" />
                  <span>Start: {new Date(contract.startDate).toLocaleDateString()}</span>
                </div>
                <div className="flex items-center">
                  <CalendarIcon className="mr-2" />
                  <span>End: {new Date(contract.endDate).toLocaleDateString()}</span>
                </div>
                <div className="flex items-center">
                  <DollarSignIcon className="mr-2" />
                  <span>Value: INR {contract.value.toLocaleString()}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          <ContractDetails contract={contract} />

          <Card className="mt-8">
            <CardHeader>
              <CardTitle className="flex items-center">
                <UsersIcon className="mr-2" />
                Employee Management
              </CardTitle>
            </CardHeader>
            <CardContent>
              <EmployeeList
                employees={employees}
                contractId={contract._id}
                onEdit={handleEditEmployee}
                onDelete={handleDeleteEmployee}
                onAdd={handleAddEmployee}
              />
            </CardContent>
          </Card>

          <Card className="mt-8">
            <CardHeader>
              <CardTitle className="flex items-center">
                <CalendarIcon className="mr-2" />
                Attendance Tracker
              </CardTitle>
            </CardHeader>
            <CardContent>
              <AttendanceTracker contractId={contract._id} />
            </CardContent>
          </Card>

          <Card className="mt-8">
            <CardHeader>
              <CardTitle className="flex items-center">
                <DollarSignIcon className="mr-2" />
                Generate Monthly Salary
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 gap-4 mb-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Month:</label>
                  <Select value={month} onValueChange={handleMonthChange}>
                    {Array.from({ length: 12 }, (_, i) => i + 1).map((m) => (
                      <Select.Option key={m} value={m}>
                        {new Date(0, m - 1).toLocaleString('default', { month: 'long' })}
                      </Select.Option>
                    ))}
                  </Select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Year:</label>
                  <Input
                    type="number"
                    value={year}
                    onChange={handleYearChange}
                    min={2000}
                    max={2100}
                  />
                </div>
              </div>
              <GenerateSalary contractId={contract._id} month={month} year={year} />
              <DisplaySalary contractId={contract._id} month={month} year={year} />
            </CardContent>
          </Card>

          <div className="mt-8 text-center">
            <Link to="/">
              <Button variant="outline">Go to Homepage</Button>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContractPage;
