// components/DisplaySalary.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DisplaySalary = ({ contractId, month, year }) => {
  const [salaries, setSalaries] = useState([]);
  const [error, setError] = useState('');

  const fetchSalaries = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/salaries`, {
        params: { month, year }
      });
      console.log('Fetched salary records:', response.data);
      setSalaries(response.data);
    } catch (error) {
      console.error('Error fetching salary records:', error);
      setError('Error fetching salary records');
    }
  };

  useEffect(() => {
    fetchSalaries();
  }, [contractId, month, year]);

  return (
    <div>
      <h3>Salary Records</h3>
      {error && <p>{error}</p>}
      {salaries.length === 0 ? (
        <p>No salary records found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Attendance</th>
              <th>Daily Wage</th>
              <th>Gross Salary</th>
              <th>Employee EPF</th>
              <th>Employee ESIC</th>
              <th>Professional Tax</th>
              <th>Total Deductions</th>
              <th>Net Salary</th>
              <th>Employer EPF</th>
              <th>Employer ESIC</th>
              <th>Admin Charges</th>
              <th>Contribution</th>
            </tr>
          </thead>
          <tbody>
            {salaries.map((salary, index) => (
              <tr key={index}>
                <td>{salary.employeeId ? salary.employeeId.name : 'Unknown'}</td>
                <td>{salary.attendance}</td>
                <td>{salary.dailyWage}</td>
                <td>{salary.grossSalary}</td>
                <td>{salary.employeeEPF}</td>
                <td>{salary.employeeESIC}</td>
                <td>{salary.pTax}</td>
                <td>{salary.totalDeductions}</td>
                <td>{salary.netSalary}</td>
                <td>{salary.employerEPF}</td>
                <td>{salary.employerESIC}</td>
                <td>{salary.adminCharges}</td>
                <td>{salary.contribution}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DisplaySalary;
