import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import Sidebar from '../components/Sidebar';
import ContractDetails from '../components/ContractDetails';
import AttendanceTracker from '../components/AttendanceTracker';
import EmployeeList from '../components/EmployeeList';
import './ContractPage.css';

const ContractPage = () => {
  const { id } = useParams();
  const [contract, setContract] = useState(null);
  const [contracts, setContracts] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    const fetchContracts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contracts');
        setContracts(response.data);
      } catch (error) {
        console.error('Error fetching contracts:', error);
      }
    };

    fetchContracts();
  }, []);

  useEffect(() => {
    const fetchContract = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${id}`);
        setContract(response.data);
      } catch (error) {
        console.error('Error fetching contract:', error);
      }
    };

    fetchContract();
  }, [id]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${id}/employees`);
        console.log('Fetched employees:', response.data); // Debugging statement
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, [id]);

  const handleSidebarToggle = (isOpen) => {
    setIsSidebarOpen(isOpen);
  };

  const handleEditEmployee = (updatedEmployee) => {
    setEmployees((prevEmployees) =>
      prevEmployees.map((emp) =>
        emp._id === updatedEmployee._id ? updatedEmployee : emp
      )
    );
  };

  const handleDeleteEmployee = async (employeeId) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contracts/${id}/employees/${employeeId}`);
        setEmployees((prevEmployees) =>
          prevEmployees.filter((emp) => emp._id !== employeeId)
        );
      } catch (error) {
        console.error('Error deleting employee:', error);
      }
    }
  };

  if (!contract) {
    return <div>Loading...</div>;
  }

  return (
    <div className={`contract-page-container ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
      <Sidebar contracts={contracts} onToggle={handleSidebarToggle} />
      <div className="contract-page">
        <div className="contract-header">
          <div className="left">
            <span>Start Date: {new Date(contract.startDate).toLocaleDateString()}</span>
            <span>End Date: {new Date(contract.endDate).toLocaleDateString()}</span>
          </div>
          <div className="middle">
            <span>Contract No.: {contract.contract_no}</span>
          </div>
          <div className="right">
            <span className="value">Value: INR {contract.value.toLocaleString()}</span>
          </div>
        </div>
        <ContractDetails contract={contract} />
        <EmployeeList
          employees={employees}
          onEdit={handleEditEmployee}
          onDelete={handleDeleteEmployee}
        />
        <AttendanceTracker contractId={contract._id} />
        <div className="navigation-links">
          <Link to="/">Go to Homepage</Link>
        </div>
      </div>
    </div>
  );
};

export default ContractPage;
