import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AttendanceForm = ({ contractId }) => {
  const [employees, setEmployees] = useState([]);
  const [attendanceData, setAttendanceData] = useState([]);
  const [selectedMonthYear, setSelectedMonthYear] = useState('');
  const [workingDays, setWorkingDays] = useState('');

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/employees`);
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, [contractId]);

  const handleAttendanceChange = (e, employeeId) => {
    const { name, value } = e.target;
    setAttendanceData((prevData) =>
      prevData.map((record) =>
        record.employeeId === employeeId ? { ...record, [name]: value } : record
      )
    );
  };

  const handleSave = async () => {
    try {
      const response = await axios.post(`http://localhost:5000/api/contracts/${contractId}/attendance`, {
        month: selectedMonthYear.split('-')[1],
        year: selectedMonthYear.split('-')[0],
        workingDays,
        attendance: attendanceData
      });
      console.log('Attendance data saved:', response.data);
    } catch (error) {
      console.error('Error saving attendance data:', error);
    }
  };

  return (
    <div className="attendance-form">
      <h3>Add Attendance</h3>
      <input
        type="month"
        value={selectedMonthYear}
        onChange={(e) => setSelectedMonthYear(e.target.value)}
      />
      <input
        type="number"
        placeholder="Working Days"
        value={workingDays}
        onChange={(e) => setWorkingDays(e.target.value)}
      />
      <table>
        <thead>
          <tr>
            <th>Employee Name</th>
            <th>Daily Wage</th>
            <th>Attendance</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee._id}>
              <td>{employee.name}</td>
              <td>{employee.dailyWage}</td>
              <td>
                <input
                  type="number"
                  name="attendance"
                  onChange={(e) => handleAttendanceChange(e, employee._id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default AttendanceForm;
