// components/GenerateSalary.js
import React, { useState } from 'react';
import axios from 'axios';

const GenerateSalary = ({ contractId }) => {
  const [month, setMonth] = useState(new Date().getMonth() + 1);
  const [year, setYear] = useState(new Date().getFullYear());
  const [message, setMessage] = useState('');

  const handleGenerateSalaries = async () => {
    try {
      console.log(`Generating salaries for contractId: ${contractId}, month: ${month}, year: ${year}`);
      const response = await axios.post(`http://localhost:5000/api/contracts/${contractId}/salaries/generate`, { month, year });
      console.log('Salaries generated:', response.data);
      setMessage('Salaries generated successfully!');
    } catch (error) {
      console.error('Error generating salaries:', error);
      setMessage('Error generating salaries. Please try again.');
    }
  };

  return (
    <div className="generate-salary">
      <h2>Generate Monthly Salary</h2>
      <div className="date-selector">
        <label>
          Month:
          <select value={month} onChange={(e) => setMonth(e.target.value)}>
            {Array.from({ length: 12 }, (_, i) => i + 1).map(m => (
              <option key={m} value={m}>{m}</option>
            ))}
          </select>
        </label>
        <label>
          Year:
          <input type="number" value={year} onChange={(e) => setYear(e.target.value)} />
        </label>
      </div>
      <button onClick={handleGenerateSalaries}>Generate Salaries</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default GenerateSalary;
