import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeDetails.css';

const EmployeeDetails = ({ contractId }) => {
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({
    name: '',
    epf: '',
    esic: '',
    bankAccount: '',
    aadhar: '',
    mobileNo: '',
    dailyWage: '',
    jobRole: '',
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);
  const [isFormOpen, setIsFormOpen] = useState(false);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/employees`);
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, [contractId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        const response = await axios.put(`http://localhost:5000/api/contracts/${contractId}/employees/${editId}`, form);
        setEmployees(employees.map(emp => (emp._id === editId ? response.data : emp)));
      } else {
        const response = await axios.post(`http://localhost:5000/api/contracts/${contractId}/employees`, form);
        setEmployees([...employees, response.data]);
      }
      setForm({
        name: '',
        epf: '',
        esic: '',
        bankAccount: '',
        aadhar: '',
        mobileNo: '',
        dailyWage: '',
        jobRole: '',
      });
      setIsEditing(false);
      setEditId(null);
      setIsFormOpen(false);
    } catch (error) {
      console.error('Error saving employee:', error);
    }
  };

  const handleEdit = (employee) => {
    setForm(employee);
    setIsEditing(true);
    setEditId(employee._id);
    setIsFormOpen(true);
  };

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this employee?');
    if (!confirmDelete) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/contracts/${contractId}/employees/${id}`);
      setEmployees(employees.filter(emp => emp._id !== id));
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  const toggleForm = () => {
    setIsFormOpen(!isFormOpen);
    if (isFormOpen) {
      setForm({
        name: '',
        epf: '',
        esic: '',
        bankAccount: '',
        aadhar: '',
        mobileNo: '',
        dailyWage: '',
        jobRole: '',
      });
      setIsEditing(false);
      setEditId(null);
    }
  };

  return (
    <div className="employee-details">
      <h2>Employee Details</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>EPF</th>
            <th>ESIC</th>
            <th>Bank Account</th>
            <th>Aadhar</th>
            <th>Mobile No.</th>
            <th>Daily Wage</th>
            <th>Job Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(emp => (
            <tr key={emp._id}>
              <td>{emp.name}</td>
              <td>{emp.epf}</td>
              <td>{emp.esic}</td>
              <td>{emp.bankAccount}</td>
              <td>{emp.aadhar}</td>
              <td>{emp.mobileNo}</td>
              <td>{emp.dailyWage}</td>
              <td>{emp.jobRole}</td>
              <td className="actions">
                <button onClick={() => handleEdit(emp)}>Edit</button>
                <button className="delete" onClick={() => handleDelete(emp._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button className="add-employee-button" onClick={toggleForm}>
        {isFormOpen ? 'Cancel' : 'Add Employee'}
      </button>
      <form className={`collapsible-form ${isFormOpen ? 'active' : ''}`} onSubmit={handleFormSubmit}>
        <input type="text" name="name" value={form.name} onChange={handleInputChange} placeholder="Name" required />
        <input type="text" name="epf" value={form.epf} onChange={handleInputChange} placeholder="EPF" required />
        <input type="text" name="esic" value={form.esic} onChange={handleInputChange} placeholder="ESIC" required />
        <input type="text" name="bankAccount" value={form.bankAccount} onChange={handleInputChange} placeholder="Bank Account" required />
        <input type="text" name="aadhar" value={form.aadhar} onChange={handleInputChange} placeholder="Aadhar" required />
        <input type="text" name="mobileNo" value={form.mobileNo} onChange={handleInputChange} placeholder="Mobile No." required />
        <input type="number" name="dailyWage" value={form.dailyWage} onChange={handleInputChange} placeholder="Daily Wage" required />
        <input type="text" name="jobRole" value={form.jobRole} onChange={handleInputChange} placeholder="Job Role" required />
        <button type="submit">{isEditing ? 'Update' : 'Add'} Employee</button>
      </form>
    </div>
  );
};

export default EmployeeDetails;
