// components/DisplaySalary.js
import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';

const DisplaySalary = ({ contractId, month, year }) => {
  const [salaries, setSalaries] = useState([]);
  const [error, setError] = useState(null);

  const fetchSalaries = useCallback(async () => {
    try {
      console.log(`Fetching salary records for contractId: ${contractId}, month: ${month}, year: ${year}`);
      const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/salaries`, {
        params: { month, year },
      });
      setSalaries(response.data);
      console.log('Fetched salary records:', response.data);
    } catch (error) {
      setError(error);
      console.error('Error fetching salary records:', error);
    }
  }, [contractId, month, year]);

  useEffect(() => {
    fetchSalaries();
  }, [fetchSalaries]);

  if (error) {
    return <div>Error fetching salary records: {error.message}</div>;
  }

  return (
    <div>
      <h3>Salary Records</h3>
      {salaries.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Attendance</th>
              <th>Daily Wage</th>
              <th>Gross Salary</th>
              <th>Employee EPF</th>
              <th>Employee ESIC</th>
              <th>P Tax</th>
              <th>Total Deductions</th>
              <th>Net Salary</th>
              <th>Employer EPF</th>
              <th>Employer ESIC</th>
              <th>Admin Charges</th>
              <th>Contribution</th>
            </tr>
          </thead>
          <tbody>
            {salaries.map((salary) => (
              <tr key={salary._id}>
                <td>{salary.employeeId ? salary.employeeId.name : 'Unknown'}</td>
                <td>{salary.attendance}</td>
                <td>{salary.dailyWage}</td>
                <td>{salary.grossSalary}</td>
                <td>{salary.employeeEPF}</td>
                <td>{salary.employeeESIC}</td>
                <td>{salary.pTax}</td>
                <td>{salary.totalDeductions}</td>
                <td>{salary.netSalary}</td>
                <td>{salary.employerEPF}</td>
                <td>{salary.employerESIC}</td>
                <td>{salary.adminCharges}</td>
                <td>{salary.contribution}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No salary records found for this period.</p>
      )}
    </div>
  );
};

export default DisplaySalary;
