import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import CompanyDetails from '../components/CompanyDetails';
import ContractList from '../components/ContractList';
import Sidebar from '../components/Sidebar';
import FileUpload from '../components/FileUpload';
import ContractsCountTracker from '../components/ContractsCountTracker';
import TotalEmployeesDisplay from '../components/TotalEmployeesDisplay'; // Import the new component
import { getContracts } from '../apis/contractApi';
import './HomePage.css'; // Import CSS for layout

const HomePage = () => {
  const [contracts, setContracts] = useState([]);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    const fetchContracts = async () => {
      try {
        const data = await getContracts();
        setContracts(data);
      } catch (error) {
        console.error('Error fetching contracts:', error);
      }
    };

    fetchContracts();
  }, []);

  const handleSidebarToggle = (isOpen) => {
    setIsSidebarOpen(isOpen);
  };

  const handleUpload = (newContract) => {
    setContracts([...contracts, newContract]);
  };

  const handleDelete = (id) => {
    setContracts(contracts.filter(contract => contract._id !== id));
  };

  return (
    <div className={`home-page ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
      <Sidebar contracts={contracts} onToggle={handleSidebarToggle} />
      <div className="main-content">
        <Header />
        <CompanyDetails />
        <FileUpload onUpload={handleUpload} />
        <ContractsCountTracker contracts={contracts} />
        <TotalEmployeesDisplay /> {/* Add the TotalEmployeesDisplay component */}
        <ContractList contracts={contracts} onDelete={handleDelete} />
      </div>
    </div>
  );
};

export default HomePage;
