import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SalaryTable.css';

const SalaryTable = ({ contractId, month, year }) => {
  const [salaries, setSalaries] = useState([]);

  useEffect(() => {
    const fetchSalaries = async () => {
      try {
        console.log(`Fetching salary records for contractId: ${contractId}, month: ${month}, year: ${year}`);
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/salaries`, {
          params: { month, year },
        });
        console.log('Fetched salary records:', response.data);
        setSalaries(response.data);
      } catch (error) {
        console.error('Error fetching salaries:', error);
      }
    };

    if (contractId) {
      fetchSalaries();
    }
  }, [contractId, month, year]);

  if (!salaries.length) {
    return <div>No salary records found for the selected month and year.</div>;
  }

  return (
    <table className="salary-table">
      <thead>
        <tr>
          <th>Employee</th>
          <th>Attendance</th>
          <th>Daily Wage</th>
          <th>Gross Salary</th>
          <th>Employee EPF (12%)</th>
          <th>Employee ESIC (0.75%)</th>
          <th>Professional Tax</th>
          <th>Total Deductions</th>
          <th>Net Salary</th>
          <th>Employer EPF (12%)</th>
          <th>Employer ESIC (3.25%)</th>
          <th>Admin Charges (0.5%)</th>
          <th>Contribution (0.5%)</th>
        </tr>
      </thead>
      <tbody>
        {salaries.map((salary) => (
          <tr key={salary.employeeId._id}>
            <td>{salary.employeeId.name}</td>
            <td>{salary.attendance}</td>
            <td>{salary.dailyWage}</td>
            <td>{salary.grossSalary}</td>
            <td>{salary.employeeEPF}</td>
            <td>{salary.employeeESIC}</td>
            <td>{salary.pTax}</td>
            <td>{salary.totalDeductions}</td>
            <td>{salary.netSalary}</td>
            <td>{salary.employerEPF}</td>
            <td>{salary.employerESIC}</td>
            <td>{salary.adminCharges}</td>
            <td>{salary.contribution}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default SalaryTable;
