import React, { useState } from 'react';
import axios from 'axios';
// import './EmployeeList.css';

const EmployeeList = ({ employees, contractId, onEdit, onDelete }) => {
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    epf: '',
    esic: '',
    bankAccount: '',
    aadhar: '',
    mobile: '',
    dailyWage: '',
    jobRole: '',
  });

  const [newEmployee, setNewEmployee] = useState({
    name: '',
    epf: '',
    esic: '',
    bankAccount: '',
    aadhar: '',
    mobile: '',
    dailyWage: '',
    jobRole: ''
  });

  const handleEditClick = (employee) => {
    setEditingEmployee(employee._id);
    setEditForm(employee);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditForm({ ...editForm, [name]: value });
  };

  const handleNewInputChange = (e) => {
    const { name, value } = e.target;
    setNewEmployee({ ...newEmployee, [name]: value });
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5000/api/contracts/${contractId}/employees/${editingEmployee}`, editForm);
      onEdit(response.data);
      setEditingEmployee(null);
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDeleteClick = async (employeeId) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contracts/${contractId}/employees/${employeeId}`);
        onDelete(employeeId);
      } catch (error) {
        console.error('Error deleting employee:', error);
      }
    }
  };

  const handleAddEmployee = async (e) => {
    e.preventDefault();
    try {
      console.log('Adding employee with data:', newEmployee);
      const response = await axios.post(`http://localhost:5000/api/contracts/${contractId}/employees`, newEmployee);
      console.log('Employee added successfully:', response.data);
      setNewEmployee({
        name: '',
        epf: '',
        esic: '',
        bankAccount: '',
        aadhar: '',
        mobile: '',
        dailyWage: '',
        jobRole: ''
      });
      onEdit(response.data); // Assuming onEdit will add the new employee to the list
    } catch (error) {
      console.error('Error adding employee:', error);
    }
  };

  return (
    <div className="employee-list">
      <h2>Employee List</h2>
      <form onSubmit={handleAddEmployee}>
        <input type="text" name="name" placeholder="Name" value={newEmployee.name} onChange={handleNewInputChange} required />
        <input type="text" name="epf" placeholder="EPF" value={newEmployee.epf} onChange={handleNewInputChange} required />
        <input type="text" name="esic" placeholder="ESIC" value={newEmployee.esic} onChange={handleNewInputChange} required />
        <input type="text" name="bankAccount" placeholder="Bank Account" value={newEmployee.bankAccount} onChange={handleNewInputChange} required />
        <input type="text" name="aadhar" placeholder="Aadhar" value={newEmployee.aadhar} onChange={handleNewInputChange} required />
        <input type="text" name="mobile" placeholder="Mobile" value={newEmployee.mobile} onChange={handleNewInputChange} required />
        <input type="number" name="dailyWage" placeholder="Daily Wage" value={newEmployee.dailyWage} onChange={handleNewInputChange} required />
        <input type="text" name="jobRole" placeholder="Job Role" value={newEmployee.jobRole} onChange={handleNewInputChange} required />
        <button type="submit">Add Employee</button>
      </form>
      {employees.map((employee) => (
        <div key={employee._id} className="employee-item">
          {editingEmployee === employee._id ? (
            <form onSubmit={handleEditSubmit}>
              <input type="text" name="name" value={editForm.name} onChange={handleInputChange} required />
              <input type="text" name="epf" value={editForm.epf} onChange={handleInputChange} required />
              <input type="text" name="esic" value={editForm.esic} onChange={handleInputChange} required />
              <input type="text" name="bankAccount" value={editForm.bankAccount} onChange={handleInputChange} required />
              <input type="text" name="aadhar" value={editForm.aadhar} onChange={handleInputChange} required />
              <input type="text" name="mobile" value={editForm.mobile} onChange={handleInputChange} required />
              <input type="number" name="dailyWage" value={editForm.dailyWage} onChange={handleInputChange} required />
              <input type="text" name="jobRole" value={editForm.jobRole} onChange={handleInputChange} required />
              <button type="submit">Save</button>
              <button type="button" onClick={() => setEditingEmployee(null)}>Cancel</button>
            </form>
          ) : (
            <div>
              <p>Name: {employee.name}</p>
              <p>EPF: {employee.epf}</p>
              <p>ESIC: {employee.esic}</p>
              <p>Bank Account: {employee.bankAccount}</p>
              <p>Aadhar: {employee.aadhar}</p>
              <p>Mobile: {employee.mobile}</p>
              <p>Daily Wage: {employee.dailyWage}</p>
              <p>Job Role: {employee.jobRole}</p>
              <button onClick={() => handleEditClick(employee)}>Edit</button>
              <button onClick={() => handleDeleteClick(employee._id)}>Delete</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default EmployeeList;
