import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './AttendanceTracker.css';

const AttendanceTracker = ({ contractId }) => {
  const [employees, setEmployees] = useState([]);
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [workingDays, setWorkingDays] = useState(0);
  const [isFormOpen, setIsFormOpen] = useState(false);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/employees`);
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, [contractId]);

  useEffect(() => {
    const fetchAttendanceRecords = async () => {
      try {
        const month = selectedDate.getMonth();
        const year = selectedDate.getFullYear();
        const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/attendance`, {
          params: { month, year }
        });
        setAttendanceRecords(response.data);
      } catch (error) {
        console.error('Error fetching attendance records:', error);
      }
    };

    fetchAttendanceRecords();
  }, [contractId, selectedDate]);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleWorkingDaysChange = (e) => {
    setWorkingDays(e.target.value);
  };

  const handleAttendanceChange = (employeeId, value) => {
    const newRecords = [...attendanceRecords];
    const index = newRecords.findIndex(record => record.employeeId === employeeId);
    if (index !== -1) {
      newRecords[index].attendance = value;
    } else {
      newRecords.push({
        contractId,
        employeeId,
        month: selectedDate.getMonth(),
        year: selectedDate.getFullYear(),
        workingDays,
        dailyWage: employees.find(emp => emp._id === employeeId).dailyWage,
        attendance: value,
      });
    }
    setAttendanceRecords(newRecords);
  };

  const handleDailyWageChange = (employeeId, value) => {
    const newRecords = [...attendanceRecords];
    const index = newRecords.findIndex(record => record.employeeId === employeeId);
    if (index !== -1) {
      newRecords[index].dailyWage = value;
    } else {
      newRecords.push({
        contractId,
        employeeId,
        month: selectedDate.getMonth(),
        year: selectedDate.getFullYear(),
        workingDays,
        dailyWage: value,
        attendance: 0,
      });
    }
    setAttendanceRecords(newRecords);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const month = selectedDate.getMonth();
      const year = selectedDate.getFullYear();
      const records = employees.map(employee => ({
        contractId,
        employeeId: employee._id,
        month,
        year,
        workingDays,
        dailyWage: attendanceRecords.find(record => record.employeeId === employee._id)?.dailyWage || employee.dailyWage,
        attendance: attendanceRecords.find(record => record.employeeId === employee._id)?.attendance || 0,
      }));
      const response = await axios.post(`http://localhost:5000/api/contracts/${contractId}/attendance`, { records });
      setAttendanceRecords(response.data);
      setIsFormOpen(false);
    } catch (error) {
      console.error('Error saving attendance records:', error);
    }
  };

  const toggleForm = () => {
    setIsFormOpen(!isFormOpen);
  };

  return (
    <div className="attendance-tracker">
      <h2>Attendance Tracker</h2>
      <button className="add-attendance-button" onClick={toggleForm}>
        {isFormOpen ? 'Cancel' : 'Add Attendance'}
      </button>
      {isFormOpen && (
        <form className="collapsible-form active" onSubmit={handleFormSubmit}>
          <label>
            Select Month/Year:
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              dateFormat="MM/yyyy"
              showMonthYearPicker
            />
          </label>
          <label>
            Number of Working Days:
            <input type="number" value={workingDays} onChange={handleWorkingDaysChange} required />
          </label>
          <table>
            <thead>
              <tr>
                <th>Employee Name</th>
                <th>Daily Wage</th>
                <th>Attendance</th>
              </tr>
            </thead>
            <tbody>
              {employees.map((employee) => (
                <tr key={employee._id}>
                  <td>{employee.name}</td>
                  <td>
                    <input
                      type="number"
                      value={attendanceRecords.find(record => record.employeeId === employee._id)?.dailyWage || employee.dailyWage}
                      onChange={(e) => handleDailyWageChange(employee._id, e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={attendanceRecords.find(record => record.employeeId === employee._id)?.attendance || 0}
                      onChange={(e) => handleAttendanceChange(employee._id, e.target.value)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button type="submit">Save Attendance</button>
        </form>
      )}
      <div className="attendance-records">
        <h3>Attendance Records</h3>
        <table>
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Daily Wage</th>
              <th>Attendance</th>
              <th>Month/Year</th>
            </tr>
          </thead>
          <tbody>
            {attendanceRecords.map(record => (
              <tr key={record._id}>
                <td>{record.employeeId.name}</td>
                <td>{record.dailyWage}</td>
                <td>{record.attendance}</td>
                <td>{`${record.month + 1}/${record.year}`}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AttendanceTracker;
