import React, { useState, useEffect } from 'react';
import { getCompanyDetails, updateCompanyDetails } from '../apis/contractApi';
import './CompanyDetails.css'; // Import the CSS file

const CompanyDetails = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [details, setDetails] = useState({
    gemSellerId: '4EEE190001039141',
    companyName: 'UNIFIED EXCELLENCE LLP',
    contactNo: '08500289313',
    email: 'unifiedexcellence2017@gmail.com',
    address: '2F-1, P 101 A, SHAKTINAGAR I MAIN, RAMAKRISHNAPURAM, TIRUMALGHERRY, SECUNDERABAD, Malkajgiri Medchal, TELANGANA-500056',
    msmeVerified: 'Yes',
    msmeRegistrationNumber: 'UDYAM-TS-09-0003611',
    mseSocialCategory: 'OBC',
    mseGender: 'Female',
    gstin: '36AAFFU0545F1ZC',
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        const data = await getCompanyDetails();
        if (data) {
          setDetails(data);
        }
      } catch (error) {
        console.error('Error fetching company details:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDetails();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDetails({ ...details, [name]: value });
  };

  const handleEditToggle = async () => {
    if (isEditing) {
      try {
        await updateCompanyDetails(details);
      } catch (error) {
        console.error('Error updating company details:', error);
      }
    }
    setIsEditing(!isEditing);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="company-details p-6 bg-white rounded-lg shadow-md">
      {isEditing ? (
        <form className="space-y-4">
          {Object.keys(details).map((key) => (
            <div key={key}>
              <label className="block text-gray-700 font-medium mb-1">
                {key.replace(/([A-Z])/g, ' $1').trim()}:
                {key === 'address' ? (
                  <textarea
                    name={key}
                    value={details[key]}
                    onChange={handleChange}
                    className="block w-full mt-1 p-2 border border-gray-300 bg-white rounded-md shadow-sm focus:border-indigo-500 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                  />
                ) : (
                  <input
                    type="text"
                    name={key}
                    value={details[key]}
                    onChange={handleChange}
                    className="block w-full mt-1 p-2 border border-gray-300 bg-white rounded-md shadow-sm focus:border-indigo-500 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                  />
                )}
              </label>
            </div>
          ))}
          <button
            type="button"
            onClick={handleEditToggle}
            className="bg-blue-500 text-white font-semibold py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-300"
          >
            Save
          </button>
        </form>
      ) : (
        <div className="space-y-4">
          {Object.keys(details).map((key) => (
            <p key={key} className="text-gray-700">
              <strong className="font-medium">{key.replace(/([A-Z])/g, ' $1').trim()}:</strong> {details[key]}
            </p>
          ))}
          <button
            onClick={handleEditToggle}
            className="bg-blue-500 text-white font-semibold py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-300"
          >
            Edit
          </button>
        </div>
      )}
    </div>
  );
};

export default CompanyDetails;
