import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SalaryTable.css';

const SalaryTable = ({ contractId, month, year }) => {
  const [salaries, setSalaries] = useState([]);

  useEffect(() => {
    const fetchSalaries = async () => {
      try {
        console.log(`Fetching salary records for contractId: ${contractId}, month: ${month}, year: ${year}`);
        const response = await axios.get('http://localhost:5000/api/salaries', {
          params: { contractId, month, year }
        });
        console.log('Fetched salary records:', response.data);
        setSalaries(response.data);
      } catch (error) {
        console.error('Error fetching salary records:', error);
      }
    };

    fetchSalaries();
  }, [contractId, month, year]);

  if (!salaries.length) {
    return <div>No salary records found.</div>;
  }

  return (
    <div className="salary-table">
      <table>
        <thead>
          <tr>
            <th>Employee Name</th>
            <th>Attendance</th>
            <th>Daily Wage</th>
            <th>Gross Salary</th>
            <th>Employee EPF</th>
            <th>Employee ESIC</th>
            <th>P Tax</th>
            <th>Total Deductions</th>
            <th>Net Salary</th>
            <th>Employer EPF</th>
            <th>Employer ESIC</th>
            <th>Admin Charges</th>
            <th>Contribution</th>
          </tr>
        </thead>
        <tbody>
          {salaries.map((salary) => (
            <tr key={salary._id}>
              <td>{salary.employeeId.name}</td>
              <td>{salary.attendance}</td>
              <td>{salary.dailyWage}</td>
              <td>{salary.grossSalary.toFixed(2)}</td>
              <td>{salary.employeeEPF.toFixed(2)}</td>
              <td>{salary.employeeESIC.toFixed(2)}</td>
              <td>{salary.pTax.toFixed(2)}</td>
              <td>{salary.totalDeductions.toFixed(2)}</td>
              <td>{salary.netSalary.toFixed(2)}</td>
              <td>{salary.employerEPF.toFixed(2)}</td>
              <td>{salary.employerESIC.toFixed(2)}</td>
              <td>{salary.adminCharges.toFixed(2)}</td>
              <td>{salary.contribution.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SalaryTable;
