import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './SalaryTable.css';

const SalaryTable = ({ contractId, month, year }) => {
  const [salaries, setSalaries] = useState([]);

  useEffect(() => {
    const fetchSalaries = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/salaries', {
          params: { contractId, month, year }
        });
        setSalaries(response.data);
      } catch (error) {
        console.error('Error fetching salaries:', error);
      }
    };

    fetchSalaries();
  }, [contractId, month, year]);

  if (salaries.length === 0) {
    return <p>No salary records found.</p>;
  }

  return (
    <table className="salary-table">
      <thead>
        <tr>
          <th>Name</th>
          <th>Category</th>
          <th>ESIC No</th>
          <th>Days Present</th>
          <th>Daily Wage</th>
          <th>Gross Pay</th>
          <th>ESI Cont. 0.75%</th>
          <th>EPF Cont. 12%</th>
          <th>P. Tax</th>
          <th>Total Deduction</th>
          <th>Net Salary</th>
          <th>EL Amt 8.33%</th>
          <th>EPF Cont. (Employer) 5%</th>
          <th>ESIC Cont. (Employer) 3.25%</th>
          <th>Acc No</th>
          <th>IFSC</th>
        </tr>
      </thead>
      <tbody>
        {salaries.map((salary, index) => (
          <tr key={index}>
            <td>{salary.name}</td>
            <td>{salary.category}</td>
            <td>{salary.esicNo}</td>
            <td>{salary.daysPresent}</td>
            <td>{salary.dailyWage}</td>
            <td>{salary.grossPay}</td>
            <td>{salary.esiCont}</td>
            <td>{salary.epfCont}</td>
            <td>{salary.pTax}</td>
            <td>{salary.totalDed}</td>
            <td>{salary.netSal}</td>
            <td>{salary.elAmt}</td>
            <td>{salary.employerEpfCont}</td>
            <td>{salary.employerEsicCont}</td>
            <td>{salary.accNo}</td>
            <td>{salary.ifsc}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default SalaryTable;
