// components/DisplaySalary.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DisplaySalary = ({ contractId, month, year }) => {
  const [salaries, setSalaries] = useState([]);
  const [error, setError] = useState(null);

  const fetchSalaries = async () => {
    try {
      console.log(`Fetching salary records for contractId: ${contractId}, month: ${month}, year: ${year}`);
      const response = await axios.get(`http://localhost:5000/api/contracts/${contractId}/salaries`, { params: { month, year } });
      setSalaries(response.data);
    } catch (error) {
      setError(error);
      console.error('Error fetching salary records:', error);
    }
  };

  useEffect(() => {
    fetchSalaries();
  }, [contractId, month, year]);

  return (
    <div>
      <h3>Salary Records for {month}/{year}</h3>
      {error && <p>Error: {error.message}</p>}
      {salaries.length === 0 ? (
        <p>No salary records found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Gross Salary</th>
              <th>Employee EPF</th>
              <th>Employee ESIC</th>
              <th>P Tax</th>
              <th>Total Deductions</th>
              <th>Net Salary</th>
              <th>Employer EPF</th>
              <th>Employer ESIC</th>
              <th>Admin Charges</th>
              <th>Contribution</th>
            </tr>
          </thead>
          <tbody>
            {salaries.map(salary => (
              <tr key={salary._id}>
                <td>{salary.employeeId.name}</td>
                <td>{salary.grossSalary}</td>
                <td>{salary.employeeEPF}</td>
                <td>{salary.employeeESIC}</td>
                <td>{salary.pTax}</td>
                <td>{salary.totalDeductions}</td>
                <td>{salary.netSalary}</td>
                <td>{salary.employerEPF}</td>
                <td>{salary.employerESIC}</td>
               
                <td>{salary.adminCharges}</td>
                <td>{salary.contribution}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DisplaySalary;
