import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SalaryTable.css';

const SalaryTable = ({ contractId, month, year }) => {
  const [salaries, setSalaries] = useState([]);

  useEffect(() => {
    const fetchSalaries = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/salaries', {
          params: { contractId, month, year }
        });
        setSalaries(response.data);
      } catch (error) {
        console.error('Error fetching salaries:', error);
      }
    };

    fetchSalaries();
  }, [contractId, month, year]);

  return (
    <table className="salary-table">
      <thead>
        <tr>
          <th>Employee Name</th>
          <th>Daily Wage</th>
          <th>Attendance</th>
          <th>Gross Pay</th>
          <th>EPF</th>
          <th>ESIC</th>
          <th>Tax</th>
          <th>Net Salary</th>
        </tr>
      </thead>
      <tbody>
        {salaries.map((salary, index) => (
          <tr key={index}>
            <td>{salary.employeeName}</td>
            <td>{salary.dailyWage}</td>
            <td>{salary.attendance}</td>
            <td>{salary.grossPay}</td>
            <td>{salary.epf}</td>
            <td>{salary.esic}</td>
            <td>{salary.tax}</td>
            <td>{salary.netSalary}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default SalaryTable;
